import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TotalCard from "./TotalCard";

export default function ViewTotal(props) {
  const user_ID = localStorage.getItem("userID");
  const [totals, setTotals] = useState([]);
  var addedTotal;
  var numbsArray = [];
  var today = new Date().toISOString();
  console.log(today);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        console.log(res.data);
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  console.log(totals);

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
    minimumFractionDigits: 2,
  });

  totals.map((numbs) => {
    var sum = 0;
    numbsArray.push(numbs.total_for_day);
    numbsArray.forEach(add);

    function add(item) {
      sum += item;
      addedTotal = sum;
    }
  });

  return (
    <div>
      <h2>Totals So far for Sarah</h2>
      {totals.map((allTotals) => {
        return (
          <TotalCard
            key={allTotals.id}
            totals={allTotals.total_for_day}
            date={today}
          />
        );
      })}
      <p>This is them all added up: {formatter.format(addedTotal)}</p>
    </div>
  );
}
