import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TotalCard from "./TotalCard";

export default function ViewTotal(props) {
  const user_ID = localStorage.getItem("userID");
  const [totals, setTotals] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        console.log(res.data);
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const Add = () => {
    totals.map((tot) => {
      let total = 0;
      let ans = total + tot.total_for_day;
      console.log(ans);
    });
  };

  return (
    <div>
      <h2>Total So far for Sarah</h2>
      {totals.map((allTotals) => {
        return (
          <TotalCard key={allTotals.id} totals={allTotals.total_for_day} />
        );
      })}
      <p>This is them all added up: ${Add()}</p>
    </div>
  );
}
