import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TotalCard from "./TotalCard";

export default function ViewTotal(props) {
  const user_ID = localStorage.getItem("userID");
  const [user, setUser] = useState([]);
  const [totals, setTotals] = useState([]);
  const [emptyTotal, setEmptyTotal] = useState(true);
  var addedTotal;
  var numbsArray = [];

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/users/${user_ID}`)
      .then((res) => {
        setUser(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        console.log(res.data);
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [user_ID]);

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
    minimumFractionDigits: 2,
  });

  totals.map((numbs) => {
    var sum = 0;
    numbsArray.push(numbs.total_for_day);
    numbsArray.forEach(add);

    function add(item) {
      sum += item;
      addedTotal = sum;
    }
  });

  if (numbsArray) {
    return (
      <div>
        <h2>Totals for {user.username}</h2>
        {totals.map((allTotals) => {
          return (
            <TotalCard key={allTotals.id} totals={allTotals.total_for_day} />
          );
        })}
        <p>Final Total: {formatter.format(addedTotal)}</p>
      </div>
    );
  } else {
    return (
      <div>
        <h2>Totals</h2>
        {totals.map((allTotals) => {
          return (
            <TotalCard key={allTotals.id} totals={allTotals.total_for_day} />
          );
        })}
        <p>Final Total: Need Totals to add!!!</p>
      </div>
    );
  }
}
