import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TotalCard from "./TotalCard";

export default function ViewTotal(props) {
  const user_ID = localStorage.getItem("userID");
  const [totals, setTotals] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  var numbsArray = [];
  const addFunction = () => {
    totals.map((numbs) => {
      var sum = 0;
      numbsArray.push(numbs.total_for_day);
      numbsArray.forEach(add);

      function add(item) {
        var newNumbs = parseFloat(item.replace(/,/g, ""));
        sum += newNumbs;
      }
    });
  };

  return (
    <div>
      <h2>Total So far for Sarah</h2>
      {totals.map((allTotals) => {
        return (
          <TotalCard key={allTotals.id} totals={allTotals.total_for_day} />
        );
      })}
      <p>This is them all added up: ${addFunction()}</p>
    </div>
  );
}
