import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TotalCard from "./TotalCard";
import "../styles/viewTotal.css";

export default function ViewTotal(props) {
  const user_ID = localStorage.getItem("userID");
  const [user, setUser] = useState([]);
  const [totals, setTotals] = useState([]);
  var addedTotal;
  var numbsArray = [];

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/users/${user_ID}`)
      .then((res) => {
        setUser(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [user_ID]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [user_ID]);

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
    minimumFractionDigits: 2,
  });

  totals.map((numbs) => {
    var sum = 0;
    numbsArray.push(numbs.total_for_day);
    numbsArray.forEach(add);

    function add(item) {
      sum += item;
      addedTotal = sum;
    }
  });

  const updateAllTotals = () => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const deleteAllTotals = () => {
    axios
      .delete(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        console.log(res);
        updateAllTotals();
      })

      .catch((err) => {
        console.log(err);
      });
  };

  var formattedTotal = formatter.format(addedTotal);

  if (!numbsArray.length) {
    return (
      <div>
        <div className="parent_title_link">
          <h2 className="user_title">Totals for {user.username}</h2>
          <Link className="create_total_link" to="/CreateTotal">
            Create Total
          </Link>
          <div>
            {totals.map((allTotals) => {
              console.log("This --> ", allTotals);
              return (
                <TotalCard
                  key={allTotals.id}
                  totals={allTotals.total_for_day}
                  date={allTotals.date}
                />
              );
            })}
          </div>
        </div>
        <p className="final_total">Need Totals to add!!!</p>
      </div>
    );
  } else {
    return (
      <div>
        <div className="parent_title_link">
          <h2 className="user_title">Totals for {user.username}</h2>
          <Link className="create_total_link" to="/CreateTotal">
            Create Total
          </Link>
        </div>
        <div className="all_cards">
          {totals.map((allTotals) => {
            return (
              <TotalCard
                key={allTotals.id}
                totals={allTotals.total_for_day}
                date={allTotals.date}
              />
            );
          })}
        </div>
        <p className="final_total">Final Total: {formattedTotal}</p>
        <button
          id="secondaryButton"
          onClick={() => {
            deleteAllTotals();
          }}
          className="delete_button"
          type="submit"
        >
          Delete All Totals
        </button>
      </div>
    );
  }
}
