import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import MonthTotalCard from "./MonthTotalCard";
import "../styles/viewTotal.css";

export default function MonthTotals(props) {
  const user_ID = localStorage.getItem("userID");
  const [user, setUser] = useState([]);
  const [totals, setTotals] = useState([]);
  var addedTotal;
  var numbsArray = [];
  var d = new Date().toLocaleString("en-US", { timeZone: "America/Denver" });

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/users/${user_ID}`)
      .then((res) => {
        setUser(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [user_ID]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [user_ID]);

  const updateAllTotals = () => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        setTotals(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const deleteAllTotals = () => {
    axios
      .delete(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        console.log(res);
        updateAllTotals();
      })

      .catch((err) => {
        console.log(err);
      });
  };

  if (!numbsArray.length) {
    return (
      <div>
        <div className="parent_title_link">
          <h2 className="user_title">Totals for {user.username}</h2>
          <Link className="create_total_link" to="/CreateTotal">
            Create Total
          </Link>
          <div>
            {totals.map((allTotals) => {
              console.log("This --> ", allTotals);
              return (
                <MonthTotalCard
                  key={allTotals.id}
                  totals={allTotals.total_for_day}
                  date={allTotals.date}
                />
              );
            })}
          </div>
        </div>
        <p className="final_total">Need Totals to add!!!</p>
      </div>
    );
  } else {
    return (
      <div>
        <div className="parent_title_link">
          <h2 className="user_title">Monthly Totals for {user.username}</h2>
        </div>
        <div className="all_cards">
          {totals.map((allTotals) => {
            return (
              <MonthTotalCard
                key={allTotals.id}
                totals={allTotals.total_for_day}
                date={allTotals.date}
              />
            );
          })}
        </div>
      </div>
    );
  }
}
