import React from "react";
import axios from "axios";
import "../styles/totalCard.css";

function MonthTotalCard(props) {
  const updateAllTotals = () => {
    axios
      .get(`http://localhost:5000/api/totals/${props.user_ID}`)
      .then((res) => {})
      .catch((err) => {
        console.log(err);
      });
  };

  const deleteTotal = () => {
    axios
      .delete(`http://localhost:5000/api/month/${props.id}`)
      .then((res) => {
        console.log(res);
      })

      .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        props.setRefresh(!props.refresh);
      });
  };

  return (
    <div className="month_main_card">
      <div className="card_parent">
        <p className="date">{props.date}</p>
        <button
          onClick={() => {
            deleteTotal();
          }}
          className="x_delete"
        >
          X
        </button>
      </div>
      <p>{props.totals}</p>
    </div>
  );
}

export default MonthTotalCard;
