import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TotalCard from "./TotalCard";

export default function ViewTotal(props) {
  const user_ID = localStorage.getItem("userID");
  const [totals, setTotal] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        setTotal(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const AddedTotals = (...nums) => {
    var s = 0;
    for (var i = 0; i < nums.length; i++) {
      s += nums[i];
    }
    console.log(s);
  };

  const Add = () => {
    totals.map((tot) => {
      return AddedTotals(parseInt(tot.total_for_day));
    });
  };

  return (
    <div>
      <h2>Total So far for Sarah</h2>
      {totals.map((allTotals) => {
        return (
          <TotalCard key={allTotals.id} totals={allTotals.total_for_day} />
        );
      })}
      <p>This is them all added up: ${Add()}</p>
    </div>
  );
}
