import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TotalCard from "./TotalCard";

export default function ViewTotal(props) {
  const user_ID = localStorage.getItem("userID");
  const [totals, setTotal] = useState([]);

  const added = totals.total_for_day;

  const AddedTotals = () => {
    let res = numbers.reduce((num1, num2) => num1 + num2);
    return res;
  };

  useEffect(() => {
    console.log("This --> ", added);
    axios
      .get(`http://localhost:5000/api/totals/${user_ID}`)
      .then((res) => {
        setTotal(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div>
      <h2>Total So far for Sarah</h2>
      {totals.map((allTotals) => {
        return (
          <TotalCard key={allTotals.id} totals={allTotals.total_for_day} />
        );
      })}
      <p>This is them all added up: {AddedTotals(numbers)}</p>
    </div>
  );
}
